mapper.insert:
insert into
board_file(
no, original_name, upload_name, file_size
) values (
#{no}, #{originalName}, #{uploadName}, #{fileSize}
)

mapper.update:
UPDATE board
SET title = #{title}, content = #{content}
WHERE no = #{no}

UPDATE board
SET view_cnt = (view_cnt + 1)
WHERE no = #{no}

mapper.select:
select
no, user_no as userNo, reaction
from board_reaction
where no = #{no}
AND user_no = #{userNo}

mapper.update:
UPDATE board_reaction
SET reaction = #{reaction}
where no = #{no}
AND user_no = #{userNo}

mapper.delete:
delete from board_reaction
where no = #{no}
AND user_no = #{userNo}
